mapper.select:
SELECT acctId ,acctNm ,acctGrpCd ,acctSt ,deptNm ,oflvNm ,acctPw ,mobile ,telephone ,email ,regr ,(SELECT acctNm FROM AcctTbl WHERE acctId = A.regr) AS regrNm ,regDtime ,modr ,modDtime ,latestDtime ,(SELECT acctPrntCd FROM AcctGrpTbl WHERE acctGrpCd = A.acctGrpCd) as authGrp FROM AcctTbl A WHERE 1 = 1
AND A.acctId = #{acctId}
AND A.acctPw = #{acctPw}
AND A.deptNm = #{deptNm}
AND A.oflvNm = #{oflvNm}
AND A.acctSt = #{acctSt}

SELECT * FROM AcctGrpTbl


        AND acctPrntCd = #{acctPrntCd}


ORDER BY acctGrpNm ASC

mapper.delete:
	DELETE FROM AcctGrpTbl


            AND acctGrpCd = #{acctGrpCd}


            AND acctPrntCd = #{acctPrntCd}

mapper.select:

SELECT ag.acctGrpCd, ag.acctGrpNm, ag.acctPrntCd, level
FROM (
    SELECT connect_by_acctGrp() AS id, @level AS level, @inclause AS inclause
    FROM (
        SELECT 
            @start_with := 0,
            @id := @start_with,
            @level := 0,
            @inclause := ''
    ) vars, AcctGrpTbl
    WHERE @id IS NOT NULL
) A
LEFT JOIN AcctGrpTbl ag ON ag.acctGrpCd = A.id
WHERE A.id IS NOT NULL
