mapper.sql:
SELECT A.bbsId ,A.bbsTit ,A.bbsCont ,A.qryCnt ,A.bbsSct ,A.fileYn ,A.regr ,(SELECT acctNm FROM AcctTbl WHERE acctId = A.regr) AS regrNm ,A.modr ,A.regDtime ,A.modDtime FROM BoardTbl A WHERE 1 = 1
AND A.bbsId = #{bbsId}
AND A.bbsSct = #{bbsSct}
AND A.bbsTit LIKE CONCAT('%', #{ searchWord }, '%')
AND A.regr IN ( SELECT acctId FROM AcctTbl WHERE acctNm LIKE CONCAT('%', #{ searchWord }, '%') )
AND A.delYn = 'n'

mapper.update:
UPDATE AppxFileTbl SET refCd = #{refCd}, fileTp = #{fileTp}, delYn = #{delYn}, fileLoc = #{fileLoc}, fileOrgNm = #{fileOrgNm}, fileSavNm = #{fileSavNm}, regr = #{regr}, regDtime = #{regDtime}, modr = #{modr}, modDtime = #{modDtime}, fileId = fileId
AND fileTp = #{fileTp}
AND fileId = #{fileId}
AND refCd = #{refCd}

mapper.select:
SELECT * FROM AppxFileTbl


        AND refCd = #{refCd}


        AND fileTp = #{fileTp}


        AND fileId = #{fileId}


ORDER BY fileId Asc

SELECT * FROM AttachTbl


        AND seqno = #{seqno}


        AND bbsId = #{bbsId}


        AND filename = #{filename}


ORDER BY seqno Asc

mapper.update:
UPDATE AttachTbl
   SET bbsId = #{bbsId}
WHERE seqno = #{seqno}

mapper.delete:
DELETE FROM AttachTbl


        AND seqno = #{seqno}


        AND bbsId = #{bbsId}


        AND filename = #{filename}

mapper.update:
UPDATE BoardTbl SET
    qryCnt = qryCnt + 1
WHERE bbsId = #{bbsId}
